# -*- coding: utf-8 -*-
def find_floor(string: str) -> int:
    floor = 0
    for direction in string:
        if direction == '(':
            floor += 1
        else:
            floor -= 1

    return floor


def find_position(string: str) -> int:
    floor = 0

    for i, s in enumerate(string):
        floor = floor + find_floor(s)
        if floor == -1:
            break
    return i+1


